) func writeLog(l *lumberjack.Logger) { outerNumber := 1000 innerNumber := 10000 for i := 0; i < outerNumber; i++ { for j := 0; j < innerNumber; j++ { ip := strconv.Itoa(rand.Intn(256)) + "." + strconv.Itoa(rand.Intn(256)) + "." + strconv.Itoa(rand.Intn(256)) + "." + strconv.Itoa(rand.Intn(256)) dataStr := fmt.Sprintf( `%s - - [%s] "%s %s HTTP/1.1" %d %d "-" "%s" "-"`, ip, time.Now().Format("02/Jan/2006:15:04:05 -0700"), Methods[rand.Intn(len(Methods))], URLs[rand.Intn(len(URLs))]+strconv.Itoa(j)+".html", StatusCodes[rand.Intn(len(StatusCodes))], rand.Intn(10000), Serives[rand.Intn(len(Serives))], ) + "\n" _, err2 := l.Write([]byte(dataStr)) if err2 != nil { return } } fmt.Printf("--write %d lines, total %d lines--\n", innerNumber, (i+1)*innerNumber) }
} func main() { l := &lumberjack.Logger{ Filename: "/var/log/nginx/access.log", MaxSize: 500, // megabytes MaxBackups: 3, MaxAge: 0, //days Compress: false, // disabled by default } for { writeLog(l) time.Sleep(10) } }
